/* Reset and Base Styles */
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
    line-height: 1.6;
    color: #333;
    background-color: #f8f9fa;
    transition: all 0.3s ease;
}

/* Arabic Font Support */
[dir="rtl"] {
    font-family: 'Segoe UI', 'Tahoma', 'Arial Unicode MS', sans-serif;
}

/* Dark Mode Variables */
:root {
    --bg-primary: #f8f9fa;
    --bg-secondary: white;
    --text-primary: #333;
    --text-secondary: #666;
    --border-color: #f0f0f0;
    --card-shadow: rgba(0, 0, 0, 0.1);
    --hero-bg: linear-gradient(135deg, #3f8439 0%, #11b023 100%);
}

[data-theme="dark"] {
    --bg-primary: #121212;
    --bg-secondary: #1e1e1e;
    --text-primary: #ffffff;
    --text-secondary: #b3b3b3;
    --border-color: #333;
    --card-shadow: rgba(0, 0, 0, 0.3);
    --hero-bg: linear-gradient(135deg, #3f8439 0%, #11b023 100%);
}

body {
    background-color: var(--bg-primary);
    color: var(--text-primary);
}

/* Header Styles */
header {
    background: var(--bg-secondary);
    color: var(--text-primary);
    padding: 1rem 0;
    box-shadow: 0 2px 10px var(--card-shadow);
    position: sticky;
    top: 0;
    z-index: 1000;
}

.navbar {
    display: flex;
    justify-content: space-between;
    align-items: center;
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 20px;
    flex-wrap: wrap;
}

.logo h2 {
    font-size: 1.8rem;
    font-weight: 700;
    letter-spacing: -0.5px;
    color: #3f8439;
}

.nav-links {
    display: flex;
    list-style: none;
    gap: 30px;
    align-items: center;
}

.nav-links a {
    color: var(--text-primary);
    text-decoration: none;
    font-weight: 500;
    padding: 8px 16px;
    border-radius: 25px;
    transition: all 0.3s ease;
    position: relative;
}

.nav-links a:hover {
    color: #3f8439;
    background: var(--bg-primary);
}

.nav-links a.active {
    background: #3f8439;
    color: white;
}

/* Header Controls */
.header-controls {
    display: flex;
    align-items: center;
    gap: 15px;
}

.dark-mode-btn,
.header-controls button {
    background: var(--bg-primary);
    border: 2px solid var(--border-color);
    color: var(--text-primary);
    padding: 8px 16px;
    border-radius: 25px;
    cursor: pointer;
    font-weight: 500;
    transition: all 0.3s ease;
    display: flex;
    align-items: center;
    gap: 5px;
}

.dark-mode-btn {
    padding: 8px 12px;
    min-width: auto;
}

.dark-mode-btn:hover,
.header-controls button:hover {
    background: #3f8439;
    color: white;
    border-color: #3f8439;
}

.dark-mode-icon {
    font-size: 1.2rem;
    transition: transform 0.3s ease;
}

[data-theme="dark"] .dark-mode-icon {
    transform: rotate(180deg);
}

/* Hero Section */
.hero {
    background: var(--hero-bg);
    color: white;
    padding: 80px 20px;
    text-align: center;
    position: relative;
    overflow: hidden;
}

.hero::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 100 100"><defs><pattern id="grain" width="100" height="100" patternUnits="userSpaceOnUse"><circle cx="50" cy="50" r="1" fill="%23ffffff" opacity="0.1"/></pattern></defs><rect width="100" height="100" fill="url(%23grain)"/></svg>') repeat;
    pointer-events: none;
}

.hero-content {
    max-width: 800px;
    margin: 0 auto;
    position: relative;
    z-index: 1;
}

.hero-title {
    font-size: 3.5rem;
    font-weight: 300;
    margin-bottom: 20px;
    letter-spacing: -1px;
}

.hero-subtitle {
    font-size: 1.3rem;
    margin-bottom: 40px;
    opacity: 0.95;
    line-height: 1.6;
}

.browse-btn {
    display: inline-flex;
    align-items: center;
    gap: 10px;
    background: white;
    color: #3f8439;
    padding: 15px 30px;
    border-radius: 30px;
    text-decoration: none;
    font-weight: 600;
    font-size: 1.1rem;
    transition: all 0.3s ease;
    box-shadow: 0 4px 15px rgba(0, 0, 0, 0.1);
}

.browse-btn:hover {
    transform: translateY(-3px);
    box-shadow: 0 8px 25px rgba(0, 0, 0, 0.2);
}

.btn-icon {
    font-size: 1.2rem;
}

/* Main Content */
main {
    max-width: 1200px;
    margin: 0 auto;
    padding: 40px 20px;
    min-height: calc(100vh - 200px);
}

/* Why Choose Section */
.why-choose {
    padding: 80px 0;
    background: var(--bg-primary);
}

.section-container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 20px;
}

.section-title {
    text-align: center;
    font-size: 2.5rem;
    color: var(--text-primary);
    margin-bottom: 60px;
    font-weight: 300;
}

.features-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    gap: 40px;
    margin-top: 40px;
}

.feature-card {
    background: var(--bg-secondary);
    padding: 30px 25px;
    border-radius: 12px;
    text-align: center;
    box-shadow: 0 4px 20px var(--card-shadow);
    transition: all 0.3s ease;
    border: 1px solid var(--border-color);
}

.feature-card:hover {
    transform: translateY(-10px);
    box-shadow: 0 15px 40px rgba(0, 0, 0, 0.15);
}

.feature-icon {
    font-size: 3rem;
    margin-bottom: 20px;
    display: block;
}

.feature-card h3 {
    font-size: 1.3rem;
    color: #3f8439;
    margin-bottom: 15px;
    font-weight: 600;
}

.feature-card p {
    color: var(--text-secondary);
    line-height: 1.6;
    font-size: 0.95rem;
}

.page-title {
    text-align: center;
    margin-bottom: 40px;
}

.page-title h1 {
    font-size: 2.5rem;
    color: #11b023;
    margin-bottom: 10px;
    font-weight: 300;
}

/* Products Page Styles */
.classification-bar {
    background: var(--bg-secondary);
    padding: 20px;
    border-radius: 15px;
    box-shadow: 0 4px 20px var(--card-shadow);
    margin-bottom: 30px;
}

.category-buttons {
    display: flex;
    gap: 15px;
    justify-content: center;
    flex-wrap: wrap;
}

.category-btn {
    padding: 12px 24px;
    border: 2px solid var(--border-color);
    background: var(--bg-secondary);
    color: var(--text-secondary);
    border-radius: 30px;
    cursor: pointer;
    font-weight: 500;
    transition: all 0.3s ease;
    font-size: 0.95rem;
}

.category-btn:hover {
    border-color: #3f8439;
    color: #3f8439;
    transform: translateY(-2px);
}

.category-btn.active {
    background: #3f8439;
    color: white;
    border-color: #3f8439;
    box-shadow: 0 4px 15px rgba(74, 144, 226, 0.3);
}

/* Best Sellers Section */
.bestsellers-section {
    padding: 80px 0;
    background: var(--bg-secondary);
}

.section-subtitle {
    text-align: center;
    font-size: 1.2rem;
    color: var(--text-secondary);
    margin-bottom: 40px;
    max-width: 600px;
    margin-left: auto;
    margin-right: auto;
}

.bestsellers-grid {
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(250px, 1fr));
    gap: 25px;
    margin-top: 40px;
}

.bestseller-card {
    background: var(--bg-secondary);
    border-radius: 12px;
    padding: 0;
    box-shadow: 0 4px 20px var(--card-shadow);
    transition: all 0.3s ease;
    border: 1px solid var(--border-color);
    overflow: hidden;
}

.bestseller-card:hover {
    transform: translateY(-6px);
    box-shadow: 0 8px 30px var(--card-shadow);
}

/* Product Images */
.product-image {
    position: relative;
    height: 160px;
    overflow: hidden;
    border-radius: 12px 12px 0 0;
    margin-bottom: 15px;
}

.product-image img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    transition: transform 0.3s ease;
}

.product-card:hover .product-image img,
.bestseller-card:hover .product-image img {
    transform: scale(1.05);
}

.product-id {
    position: absolute;
    top: 10px;
    left: 10px;
    background: rgba(255, 255, 255, 0.9);
    color: #666;
    padding: 6px 12px;
    border-radius: 20px;
    font-size: 0.85rem;
    font-weight: 600;
    backdrop-filter: blur(10px);
}

[dir="rtl"] .product-id {
    left: auto;
    right: 10px;
}

.discount-badge {
    position: absolute;
    top: 10px;
    right: 10px;
    background: #e74c3c;
    color: white;
    padding: 6px 12px;
    border-radius: 20px;
    font-size: 0.85rem;
    font-weight: 600;
}

[dir="rtl"] .discount-badge {
    left: 10px;
    right: auto;
}

/* Products Container */
.products-container {
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(280px, 1fr));
    gap: 20px;
    margin-top: 30px;
}

.product-card {
    background: var(--bg-secondary);
    border-radius: 12px;
    padding: 0;
    box-shadow: 0 4px 20px var(--card-shadow);
    transition: all 0.3s ease;
    border: 1px solid var(--border-color);
    overflow: hidden;
}

.product-card:hover {
    transform: translateY(-6px);
    box-shadow: 0 8px 30px var(--card-shadow);
}

.product-info {
    padding: 15px;
    margin-bottom: 0;
}

.product-name {
    font-size: 1.1rem;
    color: #3f8439;
    margin-bottom: 8px;
    font-weight: 600;
    line-height: 1.3;
}

.product-description {
    color: var(--text-secondary);
    margin-bottom: 12px;
    line-height: 1.5;
    font-size: 0.9rem;
}

.product-price {
    font-size: 1.2rem;
    font-weight: 700;
    color: #27ae60;
    margin-bottom: 15px;
}

/* Price Section for Best Sellers */
.price-section {
    display: flex;
    align-items: center;
    gap: 10px;
    margin-bottom: 20px;
}

.current-price {
    font-size: 1.2rem;
    font-weight: 700;
    color: #27ae60;
}

.original-price {
    font-size: 1rem;
    color: var(--text-secondary);
    text-decoration: line-through;
    opacity: 0.7;
}

/* WhatsApp Button */
.whatsapp-btn {
    background: linear-gradient(135deg, #25d366 0%, #128c7e 100%);
    color: white;
    border: none;
    padding: 10px 20px;
    border-radius: 0 0 12px 12px;
    cursor: pointer;
    font-weight: 500;
    transition: all 0.3s ease;
    width: 100%;
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 6px;
    font-size: 0.9rem;
}

.whatsapp-btn:hover {
    background: linear-gradient(135deg, #128c7e 0%, #075e54 100%);
    transform: translateY(-2px);
    box-shadow: 0 6px 20px rgba(37, 211, 102, 0.4);
}

.whatsapp-icon {
    font-size: 1.2rem;
}

/* Load More Button */
.load-more-container {
    display: flex;
    justify-content: center;
    margin: 40px 0;
}

.load-more-btn {
    background: linear-gradient(135deg, #3f8439 0%, #11b023 100%);
    color: white;
    border: none;
    padding: 15px 30px;
    border-radius: 25px;
    font-size: 1rem;
    font-weight: 600;
    cursor: pointer;
    transition: all 0.3s ease;
    display: flex;
    align-items: center;
    gap: 8px;
}

.load-more-btn:hover {
    transform: translateY(-2px);
    box-shadow: 0 8px 25px rgba(63, 132, 57, 0.3);
}

.load-more-btn:disabled {
    background: var(--text-secondary);
    cursor: not-allowed;
    transform: none;
    box-shadow: none;
}

/* Page Content Styles */
.content-container {
    background: var(--bg-secondary);
    border-radius: 15px;
    padding: 40px;
    box-shadow: 0 6px 25px var(--card-shadow);
    margin-bottom: 30px;
}

/* About Page Styles */

.about-section {
    margin-bottom: 40px;
}

.about-section:last-child {
    margin-bottom: 0;
}

.about-section h2 {
    color: #3f8439;
    font-size: 1.8rem;
    margin-bottom: 20px;
    font-weight: 600;
    border-bottom: 3px solid #3f8439;
    padding-bottom: 10px;
    display: inline-block;
}

.about-section p {
    font-size: 1.1rem;
    line-height: 1.8;
    color: #555;
    margin-bottom: 15px;
}

.values-list {
    list-style: none;
    padding: 0;
}

.values-list li {
    background: #f8f9fa;
    margin-bottom: 15px;
    padding: 20px;
    border-radius: 10px;
    border-left: 4px solid #3f8439;
    font-size: 1.05rem;
    transition: all 0.3s ease;
}

[dir="rtl"] .values-list li {
    border-left: none;
    border-right: 4px solid #3f8439;
}

.values-list li:hover {
    background: #f0f7ff;
    transform: translateX(5px);
}

[dir="rtl"] .values-list li:hover {
    transform: translateX(-5px);
}

/* Contact Page Styles */
.contact-info {
    margin-bottom: 40px;
}

.contact-section {
    margin-bottom: 30px;
}

.contact-section h2 {
    color: #2c5aa0;
    font-size: 1.8rem;
    margin-bottom: 15px;
    font-weight: 600;
}

.contact-section p {
    font-size: 1.1rem;
    line-height: 1.8;
    color: #555;
}

.contact-details {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 25px;
    margin-top: 30px;
}

.contact-item {
    background: #f8f9fa;
    padding: 25px;
    border-radius: 12px;
    text-align: center;
    transition: all 0.3s ease;
}

.contact-item:hover {
    background: #f0f7ff;
    transform: translateY(-5px);
}

.contact-item h3 {
    color: #3f8439;
    font-size: 1.2rem;
    margin-bottom: 10px;
    font-weight: 600;
}

.contact-item p {
    color: #666;
    font-size: 1rem;
    margin: 5px 0;
}

.contact-form-section {
    margin-top: 40px;
    padding-top: 40px;
    border-top: 2px solid #f0f0f0;
}

.contact-form-section h2 {
    color: #3f8439;
    font-size: 1.8rem;
    margin-bottom: 25px;
    font-weight: 600;
    text-align: center;
}

.contact-form {
    max-width: 600px;
    margin: 0 auto;
}

.form-group {
    margin-bottom: 25px;
}

.form-group label {
    display: block;
    margin-bottom: 8px;
    color: #333;
    font-weight: 500;
    font-size: 1rem;
}

.form-group input,
.form-group select,
.form-group textarea {
    width: 100%;
    padding: 12px 16px;
    border: 2px solid #e1e5e9;
    border-radius: 8px;
    font-size: 1rem;
    transition: all 0.3s ease;
    background: white;
}

.form-group input:focus,
.form-group select:focus,
.form-group textarea:focus {
    outline: none;
    border-color: #3f8439;
    box-shadow: 0 0 0 3px rgba(74, 144, 226, 0.1);
}

.submit-btn {
    background: linear-gradient(135deg, #3f8439 0%, #11b023 100%);
    color: white;
    border: none;
    padding: 15px 40px;
    border-radius: 25px;
    font-size: 1.1rem;
    font-weight: 600;
    cursor: pointer;
    transition: all 0.3s ease;
    display: block;
    margin: 30px auto 0;
}

.submit-btn:hover {
    transform: translateY(-2px);
    box-shadow: 0 6px 20px rgba(74, 144, 226, 0.4);
}

/* Footer */
footer {
    background: var(--bg-secondary);
    color: var(--text-primary);
    text-align: center;
    padding: 20px;
    margin-top: 50px;
    border-top: 1px solid var(--border-color);
}

footer p {
    font-size: 0.95rem;
}

/* Responsive Design */
@media (max-width: 768px) {
    .navbar {
        flex-direction: column;
        gap: 15px;
        padding: 15px;
    }

    .nav-links {
        gap: 15px;
    }

    .page-title h1 {
        font-size: 2rem;
    }

    .products-container {
        grid-template-columns: 1fr;
        gap: 20px;
    }

    .category-buttons {
        flex-direction: column;
        align-items: center;
    }

    .category-btn {
        width: 100%;
        max-width: 300px;
    }

    .content-container {
        padding: 25px;
    }

    .contact-details {
        grid-template-columns: 1fr;
    }
}

@media (max-width: 480px) {
    main {
        padding: 20px 15px;
    }

    .page-title h1 {
        font-size: 1.6rem;
    }

    .product-card {
        padding: 20px;
    }

    .content-container {
        padding: 20px;
    }
}

/* RTL (Arabic) Specific Styles */
[dir="rtl"] {
    text-align: right;
}

[dir="rtl"] .navbar {
    direction: rtl;
}

[dir="rtl"] .nav-links {
    direction: rtl;
}

[dir="rtl"] .category-buttons {
    direction: rtl;
}

[dir="rtl"] .products-container {
    direction: rtl;
}

[dir="rtl"] .contact-details {
    direction: rtl;
}

[dir="rtl"] .form-group {
    text-align: right;
}

[dir="rtl"] .form-group input,
[dir="rtl"] .form-group select,
[dir="rtl"] .form-group textarea {
    text-align: right;
}

/* Loading Animation */
@keyframes fadeInUp {
    from {
        opacity: 0;
        transform: translateY(30px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

.product-card,
.contact-item,
.about-section {
    animation: fadeInUp 0.6s ease forwards;
}



/* social icon  */


 .social-icons {
      position: fixed;
      top: 50%;
      left: 20px;
      transform: translateY(-50%);
      display: flex;
      flex-direction: column;
      gap: 15px;
    }
    .social-icons a {
      text-decoration: none;
      font-size: 28px;
      color: white;
      background: #333;
      width: 45px;
      height: 45px;
      border-radius: 50%;
      display: flex;
      align-items: center;
      justify-content: center;
      transition: 0.3s;
    }
    .social-icons a:hover {
      background: #007bff;
      transform: scale(1.1);
    }

    /* Search container */
.search-container {
    margin: 2rem 0;
    display: flex;
    justify-content: center;
}

.search-box {
    display: flex;
    gap: 1rem;
    align-items: center;
    max-width: 500px;
    width: 100%;
}

.search-box input {
    flex: 1;
    padding: 0.8rem 1rem;
    border: 2px solid var(--border-color);
    border-radius: 25px;
    font-size: 1rem;
    background-color: var(--bg-color);
    color: var(--text-color);
    transition: all 0.3s ease;
}

.search-box input:focus {
    outline: none;
    border-color: var(--primary-color);
    box-shadow: 0 0 0 3px rgba(44, 90, 160, 0.1);
}

.search-box button {
    padding: 0.8rem 1.5rem;
    background-color: var(--primary-color);
    color: blue;
    border: none;
    border-radius: 20px;
    font-size: 0.9rem;
    cursor: pointer;
    transition: all 0.3s ease;
}

.search-box button:hover {
    background-color: #3f8439;
    transform: translateY(-1px);
}

/* No products message */
.no-products {
    text-align: center;
    padding: 40px 20px;
    color: var(--text-secondary);
    font-size: 1.1rem;
}

/* Loading state */
.loading {
    text-align: center;
    padding: 20px;
    color: var(--text-secondary);
}

.loading::after {
    content: "...";
    animation: loading 1.5s infinite;
}

@keyframes loading {
    0%, 20% { opacity: 0; }
    50% { opacity: 1; }
    80%, 100% { opacity: 0; }
}

