    document.getElementById('secondaryCategory').addEventListener('change', onSecondaryCategoryChange);
    document.getElementById('productImage').addEventListener('change', handleImageSelect);
}

function handleImageSelect(e) {
    const file = e.target.files[0];
    if (file) {
        const reader = new FileReader();
        reader.onload = function(event) {
            document.getElementById('imagePreview').style.display = 'block';
            document.getElementById('previewImg').src = event.target.result;
        };
        reader.readAsDataURL(file);
    } else {
        document.getElementById('imagePreview').style.display = 'none';
    }
}

function onPrimaryCategoryChange(e) {
    const primaryValue = e.target.value;
    const secondarySelect = document.getElementById('secondaryCategory');
    const tertiarySelect = document.getElementById('tertiaryCategory');

    if (!primaryValue) {
        secondarySelect.disabled = true;
        secondarySelect.innerHTML = '<option value="">ÿßÿÆÿ™ÿ± ÿßŸÑŸÅÿ¶ÿ© ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿäÿ© ÿ£ŸàŸÑÿßŸã</option>';
        tertiarySelect.disabled = true;
        tertiarySelect.innerHTML = '<option value="">ÿßÿÆÿ™ÿ± ÿßŸÑŸÅÿ¶ÿ© ÿßŸÑŸÅÿ±ÿπŸäÿ© ÿ£ŸàŸÑÿßŸã</option>';
        return;
    }

    const subcategories = CATEGORIES_DATA[primaryValue].subcategories;
    secondarySelect.disabled = false;
    secondarySelect.innerHTML = '<option value="">ÿßÿÆÿ™ÿ± ÿßŸÑŸÅÿ¶ÿ© ÿßŸÑŸÅÿ±ÿπŸäÿ©</option>' +
        subcategories.map(sub => `<option value="${sub.key}">${sub.name.ar}</option>`).join('');

    tertiarySelect.disabled = true;
    tertiarySelect.innerHTML = '<option value="">ÿßÿÆÿ™ÿ± ÿßŸÑŸÅÿ¶ÿ© ÿßŸÑŸÅÿ±ÿπŸäÿ© ÿ£ŸàŸÑÿßŸã</option>';
}

function onSecondaryCategoryChange(e) {
    const primaryValue = document.getElementById('primaryCategory').value;
    const secondaryValue = e.target.value;
    const tertiarySelect = document.getElementById('tertiaryCategory');

    if (!secondaryValue || !primaryValue) {
        tertiarySelect.disabled = true;
        tertiarySelect.innerHTML = '<option value="">ÿßÿÆÿ™ÿ± ÿßŸÑŸÅÿ¶ÿ© ÿßŸÑŸÅÿ±ÿπŸäÿ© ÿ£ŸàŸÑÿßŸã</option>';
        return;
    }

    const subcategory = CATEGORIES_DATA[primaryValue].subcategories.find(sub => sub.key === secondaryValue);
    if (subcategory && subcategory.tertiary) {
        tertiarySelect.disabled = false;
        tertiarySelect.innerHTML = '<option value="">ÿßÿÆÿ™ÿ± ÿßŸÑŸÅÿ¶ÿ© ÿßŸÑÿ™ŸÅÿµŸäŸÑŸäÿ©</option>' +
            subcategory.tertiary.map(tert => `<option value="${tert.key}">${tert.name.ar}</option>`).join('');
