function getTertiaryName(key) {
    // Add tertiary category names here if needed
    return key;
}

function filterProducts() {
    const searchTerm = document.getElementById('searchInput').value.toLowerCase();

    const filtered = allProducts.filter(product => {
        const nameAr = product.name?.ar?.toLowerCase() || '';
        const nameEn = product.name?.en?.toLowerCase() || '';
        const descAr = product.description?.ar?.toLowerCase() || '';
        const descEn = product.description?.en?.toLowerCase() || '';
        const id = product.id?.toLowerCase() || '';

        return nameAr.includes(searchTerm) || 
               nameEn.includes(searchTerm) || 
               descAr.includes(searchTerm) || 
               descEn.includes(searchTerm) ||
               id.includes(searchTerm);
    });

    renderProducts(filtered);
}

function renderProducts(products) {
