// Language and theme state
let currentLang = localStorage.getItem("lang") || "en";
let isDarkMode = false;

// Data variables - will be loaded from JSON
let classificationConfig = {};
let products = [];
let bestSellingProducts = [];
let searchTerm = '';

// Pagination variables
const PRODUCTS_PER_PAGE = 20;
let currentPage = 1;
let displayedProducts = [];
let allFilteredProducts = [];

// Load data from JSON files
async function loadData() {
    try {
        const [productsResponse, categoriesResponse] = await Promise.all([
            fetch('/api/products'),
            fetch('/api/categories')
        ]);

        if (!productsResponse.ok || !categoriesResponse.ok) {
            throw new Error('API request failed, falling back to JSON files');
        }

        const rawProducts = await productsResponse.json();
        classificationConfig = await categoriesResponse.json();

        // Filter and validate products data
        products = rawProducts.filter(product => 
            product && product.id && product.name && product.primaryCategory
        );

        // Build bestsellers array from products data (products with isBestseller flag)
        bestSellingProducts = products
            .filter(product => product.isBestseller)
            .map(product => ({
                ...product,
                originalPrice: product.price,
                discount: product.discountPercent || 0
            }));

        console.log('Data loaded successfully from API');
        return true;
    } catch (error) {
        console.error('Error loading data from API, trying fallback:', error);

        // Fallback to JSON files if API fails
        try {
            const [productsResponse, categoriesResponse, bestsellersResponse] = await Promise.all([
                fetch('data/products.json'),
                fetch('data/categories.json'),
                fetch('data/bestsellers.json')
            ]);

            const rawProducts = await productsResponse.json();
            classificationConfig = await categoriesResponse.json();
            const bestsellersData = await bestsellersResponse.json();

            // Filter and validate products data
            products = rawProducts.filter(product => 
                product && product.id && product.name && product.description && product.category
            );

            // Build bestsellers array from products and bestsellers data
            bestSellingProducts = bestsellersData
                .map(bestseller => {
                    const product = products.find(p => p.id === bestseller.productId);
                    if (!product) return null;
                    return {
                        ...product,
                        originalPrice: bestseller.originalPrice,
                        discount: bestseller.discount
                    };
                })
                .filter(item => item !== null);

            console.log('Data loaded successfully from JSON fallback');
            return true;
        } catch (fallbackError) {
            console.error('Error loading fallback data:', fallbackError);
            return false;
        }
    }
}

// Current filter state
let currentPrimaryCategory = null;
let currentSecondaryCategory = null;
let currentTertiaryCategory = null;
let hasSelectedCategory = false;

// Language switching functionality
function toggleLanguage() {
    currentLang = currentLang === "en" ? "ar" : "en";
    localStorage.setItem("lang", currentLang);

    const html = document.documentElement;
    if (currentLang === "ar") {
      html.setAttribute("lang", "ar");
      html.setAttribute("dir", "rtl");
    } else {
      html.setAttribute("lang", "en");
      html.setAttribute("dir", "ltr");
    }

    updateTextContent();
}

// Dark mode functionality
function toggleDarkMode() {
    isDarkMode = !isDarkMode;
    const html = document.documentElement;

    if (isDarkMode) {
      html.setAttribute("data-theme", "dark");
      localStorage.setItem("darkMode", "true");
    } else {
      html.setAttribute("data-theme", "light");
      localStorage.setItem("darkMode", "false");
    }

    updateDarkModeIcon();
}

// Update dark mode icon
function updateDarkModeIcon() {
    const darkModeIcon = document.querySelector(".dark-mode-icon");
    if (darkModeIcon) {
      darkModeIcon.textContent = isDarkMode ? "‚òÄÔ∏è" : "üåô";
    }
}

// Initialize dark mode from localStorage (called early to prevent FOUC)
function initializeDarkMode() {
    const savedMode = localStorage.getItem('darkMode');
    const html = document.documentElement;

    if (savedMode === 'true') {
        isDarkMode = true;
        html.setAttribute('data-theme', 'dark');
    } else {
        isDarkMode = false;
        html.setAttribute('data-theme', 'light');
    }

    // Update icon after DOM is ready
    setTimeout(() => updateDarkModeIcon(), 0);
}

// Initialize theme immediately to prevent FOUC
(function() {
    const savedMode = localStorage.getItem('darkMode');
    if (savedMode === 'true') {
        document.documentElement.setAttribute('data-theme', 'dark');
    } else {
        document.documentElement.setAttribute('data-theme', 'light');
    }
})();

// Update all text content based on current language
function updateTextContent() {
    const elements = document.querySelectorAll('[data-en][data-ar]');
    elements.forEach(element => {
        if (currentLang === 'ar') {
            if (element.children.length === 0) {
                element.textContent = element.getAttribute('data-ar');
            } else {
                element.innerHTML = element.getAttribute('data-ar');
            }
        } else {
            if (element.children.length === 0) {
                element.textContent = element.getAttribute('data-en');
            } else {
                element.innerHTML = element.getAttribute('data-en');
            }
        }
    });

    // Update form placeholders and options if on contact page
    updateFormContent();

    // Re-render products and update classification bars if secondary bar is visible
    if (document.getElementById('primaryBar')) {
        if (currentSecondaryCategory && document.getElementById('secondaryBar').style.display !== 'none') {
            populateSecondaryBar(currentPrimaryCategory);
        }
        if (currentTertiaryCategory && document.getElementById('tertiaryBar').style.display !== 'none') {
            populateTertiaryBar(currentPrimaryCategory, currentSecondaryCategory);
        }
        // Display products if a category has been selected OR if search is active
        if (hasSelectedCategory || searchTerm) {
            displayProducts();
        }
    }
}

function updateFormContent() {
    // Update search input placeholder
    const searchInput = document.getElementById('searchInput');
    if (searchInput) {
        const placeholder = currentLang === 'ar' ? 'ÿßŸÑÿ®ÿ≠ÿ´ ÿπŸÜ ÿßŸÑŸÖŸÜÿ™ÿ¨ÿßÿ™...' : 'Search products...';
        searchInput.placeholder = placeholder;
    }
}

// Search functionality with debouncing
let searchTimeout;
function performSearch() {
    clearTimeout(searchTimeout);
    searchTimeout = setTimeout(() => {
        const searchInput = document.getElementById('searchInput');
        searchTerm = searchInput.value.toLowerCase().trim();

        // Reset category selection if searching
        if (searchTerm) {
            currentPrimaryCategory = null;
            currentSecondaryCategory = null;
            currentTertiaryCategory = null;
            hasSelectedCategory = false;

            // Hide classification bars when searching
            const primaryBar = document.getElementById('primaryBar');
            const secondaryBar = document.getElementById('secondaryBar');
            const tertiaryBar = document.getElementById('tertiaryBar');
            if (primaryBar) primaryBar.style.display = 'none';
            if (secondaryBar) secondaryBar.style.display = 'none';
            if (tertiaryBar) tertiaryBar.style.display = 'none';
        } else {
            // Show primary bar when search is cleared
            const primaryBar = document.getElementById('primaryBar');
            if (primaryBar) primaryBar.style.display = 'block';
        }

        resetPagination();
        displayProducts();
    }, 300);
}

function clearSearch() {
    const searchInput = document.getElementById('searchInput');
    searchInput.value = '';
    searchTerm = '';
    currentPrimaryCategory = null;
    currentSecondaryCategory = null;
    currentTertiaryCategory = null;
    hasSelectedCategory = false;

    // Show primary bar
    const primaryBar = document.getElementById('primaryBar');
    const secondaryBar = document.getElementById('secondaryBar');
    const tertiaryBar = document.getElementById('tertiaryBar');
    if (primaryBar) primaryBar.style.display = 'block';
    if (secondaryBar) secondaryBar.style.display = 'none';
    if (tertiaryBar) tertiaryBar.style.display = 'none';

    resetPagination();

    // Show initial message
    const container = document.getElementById('productsContainer');
    if (container && primaryBar) {
        container.innerHTML = `
            <div class="no-products">
                <p data-en="Please select a category above to view products" data-ar="Ÿäÿ±ÿ¨Ÿâ ÿßÿÆÿ™Ÿäÿßÿ± ŸÅÿ¶ÿ© ÿ£ÿπŸÑÿßŸá ŸÑÿπÿ±ÿ∂ ÿßŸÑŸÖŸÜÿ™ÿ¨ÿßÿ™">
                    ${currentLang === 'ar' ? 'Ÿäÿ±ÿ¨Ÿâ ÿßÿÆÿ™Ÿäÿßÿ± ŸÅÿ¶ÿ© ÿ£ÿπŸÑÿßŸá ŸÑÿπÿ±ÿ∂ ÿßŸÑŸÖŸÜÿ™ÿ¨ÿßÿ™' : 'Please select a category above to view products'}
                </p>
            </div>
        `;
    }
}

function filterProductsBySearch(products) {
    if (!searchTerm) return products;

    return products.filter(product => {
        const name = product.name[currentLang].toLowerCase();
        const description = product.description[currentLang].toLowerCase();
        const id = product.id.toLowerCase();

        return name.includes(searchTerm) || 
               description.includes(searchTerm) || 
               id.includes(searchTerm);
    });
}

function setupSearchListeners() {
    const searchInput = document.getElementById('searchInput');
    const clearButton = document.getElementById('clearSearch');

    if (searchInput) {
        searchInput.addEventListener('input', performSearch);
        searchInput.addEventListener('keyup', function(e) {
            if (e.key === 'Escape') {
                clearSearch();
            }
        });
    }

    if (clearButton) {
        clearButton.addEventListener('click', clearSearch);
    }
}

// WhatsApp functionality
function openWhatsAppOrder(productId) {
    const whatsappNumber = "+9647819914700"; // Replace with actual WhatsApp business number
    const message = currentLang === 'ar' 
        ? `ŸÖÿ±ÿ≠ÿ®ÿß! ÿ£ŸàÿØ ÿ∑ŸÑÿ® ÿßŸÑŸÖŸÜÿ™ÿ¨ ÿ±ŸÇŸÖ: ${productId}` 
        : `Hello! I would like to order product ID: ${productId}`;

    const whatsappUrl = `https://wa.me/${whatsappNumber.replace('+', '')}?text=${encodeURIComponent(message)}`;
    window.open(whatsappUrl, '_blank');
}

// Primary category selection
function selectPrimaryCategory(primaryCategory) {
    currentPrimaryCategory = primaryCategory;
    currentSecondaryCategory = null;
    currentTertiaryCategory = null;
    hasSelectedCategory = true;

    // Update primary buttons
    document.querySelectorAll('.primary-btn').forEach(btn => btn.classList.remove('active'));
    document.querySelector(`[data-primary="${primaryCategory}"]`).classList.add('active');

    // Hide primary bar and show secondary bar
    document.getElementById('primaryBar').style.display = 'none';
    document.getElementById('secondaryBar').style.display = 'block';
    document.getElementById('tertiaryBar').style.display = 'none';

    // Populate secondary bar
    populateSecondaryBar(primaryCategory);

    resetPagination();
    // Display all products for this primary category
    displayProducts();
}

// Populate secondary classification bar
function populateSecondaryBar(primaryCategory) {
    const secondaryBar = document.getElementById('secondaryBar');
    const categoryButtons = secondaryBar.querySelector('.category-buttons');

    const config = classificationConfig[primaryCategory];
    if (!config) return;

    categoryButtons.innerHTML = config.subcategories.map(subcat => `
        <button class="category-btn secondary-btn" 
                data-secondary="${subcat.key}" 
                data-en="${subcat.name.en}" 
                data-ar="${subcat.name.ar}"
                onclick="selectSecondaryCategory('${subcat.key}')">
            ${subcat.name[currentLang]}
        </button>
    `).join('') + `
        <button class="category-btn secondary-btn back-btn" 
                data-en="Back" 
                data-ar="ÿ±ÿ¨Ÿàÿπ"
                onclick="goBackToPrimary()">
            ${currentLang === 'ar' ? 'ÿ±ÿ¨Ÿàÿπ' : 'Back'}
        </button>
    `;

    // Restore active state for currently selected secondary category
    if (currentSecondaryCategory) {
        const activeBtn = categoryButtons.querySelector(`[data-secondary="${currentSecondaryCategory}"]`);
        if (activeBtn) {
            activeBtn.classList.add('active');
        }
    }
}

// Secondary category selection
function selectSecondaryCategory(secondaryCategory) {
    currentSecondaryCategory = secondaryCategory;
    currentTertiaryCategory = null;

    // Update secondary buttons
    document.querySelectorAll('.secondary-btn').forEach(btn => btn.classList.remove('active'));
    document.querySelector(`[data-secondary="${secondaryCategory}"]`).classList.add('active');

    // Hide secondary bar and show tertiary bar
    document.getElementById('secondaryBar').style.display = 'none';
    document.getElementById('tertiaryBar').style.display = 'block';

    // Populate tertiary bar
    populateTertiaryBar(currentPrimaryCategory, secondaryCategory);

    resetPagination();
    // Display filtered products
    displayProducts();
}

// Populate tertiary classification bar
function populateTertiaryBar(primaryCategory, secondaryCategory) {
    const tertiaryBar = document.getElementById('tertiaryBar');
    const categoryButtons = tertiaryBar.querySelector('.category-buttons');

    const config = classificationConfig[primaryCategory];
    if (!config) return;

    const subCategory = config.subcategories.find(sub => sub.key === secondaryCategory);
    if (!subCategory || !subCategory.tertiary) return;

    categoryButtons.innerHTML = subCategory.tertiary.map(tertiary => `
        <button class="category-btn tertiary-btn" 
                data-tertiary="${tertiary.key}" 
                data-en="${tertiary.name.en}" 
                data-ar="${tertiary.name.ar}"
                onclick="selectTertiaryCategory('${tertiary.key}')">
            ${tertiary.name[currentLang]}
        </button>
    `).join('') + `
        <button class="category-btn tertiary-btn back-btn" 
                data-en="Back" 
                data-ar="ÿ±ÿ¨Ÿàÿπ"
                onclick="goBackToSecondary()">
            ${currentLang === 'ar' ? 'ÿ±ÿ¨Ÿàÿπ' : 'Back'}
        </button>
    `;

    // Restore active state for currently selected tertiary category
    if (currentTertiaryCategory) {
        const activeBtn = categoryButtons.querySelector(`[data-tertiary="${currentTertiaryCategory}"]`);
        if (activeBtn) {
            activeBtn.classList.add('active');
        }
    }
}

// Tertiary category selection
function selectTertiaryCategory(tertiaryCategory) {
    currentTertiaryCategory = tertiaryCategory;

    // Update tertiary buttons
    document.querySelectorAll('.tertiary-btn').forEach(btn => btn.classList.remove('active'));
    document.querySelector(`[data-tertiary="${tertiaryCategory}"]`).classList.add('active');

    resetPagination();
    // Display filtered products
    displayProducts();
}

// Go back to secondary categories
function goBackToSecondary() {
    currentTertiaryCategory = null;

    // Show secondary bar and hide tertiary bar
    document.getElementById('secondaryBar').style.display = 'block';
    document.getElementById('tertiaryBar').style.display = 'none';

    resetPagination();
    // Display all products for current secondary category
    displayProducts();
}

// Go back to primary categories
function goBackToPrimary() {
    currentSecondaryCategory = null;
    currentTertiaryCategory = null;

    // Show primary bar and hide secondary/tertiary bars
    document.getElementById('primaryBar').style.display = 'block';
    document.getElementById('secondaryBar').style.display = 'none';
    document.getElementById('tertiaryBar').style.display = 'none';

    resetPagination();
    // Display all products for current primary category
    displayProducts();
}

// Pagination variables
let isLoading = false;
let hasMoreProducts = true;
let scrollObserver = null;

// Pagination functions
function resetPagination() {
    currentPage = 1;
    displayedProducts = [];
    hasMoreProducts = true;
    isLoading = false;

    // Remove existing observer
    if (scrollObserver) {
        scrollObserver.disconnect();
        scrollObserver = null;
    }
}

function loadMoreProducts() {
    if (isLoading || !hasMoreProducts) return;

    isLoading = true;
    showLoadingIndicator();

    // Simulate loading delay for smooth UX
    setTimeout(() => {
        const startIndex = currentPage * PRODUCTS_PER_PAGE;
        const endIndex = startIndex + PRODUCTS_PER_PAGE;
        const newProducts = allFilteredProducts.slice(startIndex, endIndex);

        if (newProducts.length > 0) {
            displayedProducts = displayedProducts.concat(newProducts);
            currentPage++;
            renderProducts(displayedProducts);

            // Check if there are more products
            if (displayedProducts.length >= allFilteredProducts.length) {
                hasMoreProducts = false;
            }

            // Re-setup scroll observer for new products
            setupScrollObserver();
        } else {
            hasMoreProducts = false;
        }

        isLoading = false;
        hideLoadingIndicator();
    }, 300);
}

function showLoadingIndicator() {
    const container = document.getElementById('productsContainer');
    if (!container) return;

    const loadingDiv = document.createElement('div');
    loadingDiv.id = 'loadingIndicator';
    loadingDiv.className = 'loading';
    loadingDiv.innerHTML = currentLang === 'ar' 
        ? 'ÿ¨ÿßÿ±Ÿä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑŸÖÿ≤ŸäÿØ...' 
        : 'Loading more products...';

    container.appendChild(loadingDiv);
}

function hideLoadingIndicator() {
    const loadingDiv = document.getElementById('loadingIndicator');
    if (loadingDiv) {
        loadingDiv.remove();
    }
}

function setupScrollObserver() {
    // Remove existing observer
    if (scrollObserver) {
        scrollObserver.disconnect();
    }

    if (!hasMoreProducts) return;

    // Create a sentinel element at the bottom of the products container
    const container = document.getElementById('productsContainer');
    if (!container) return;

    // Remove existing sentinel
    const existingSentinel = document.getElementById('scrollSentinel');
    if (existingSentinel) {
        existingSentinel.remove();
    }

    const sentinel = document.createElement('div');
    sentinel.id = 'scrollSentinel';
    sentinel.style.height = '1px';
    sentinel.style.width = '100%';
    container.appendChild(sentinel);

    // Create intersection observer
    scrollObserver = new IntersectionObserver((entries) => {
        entries.forEach(entry => {
            if (entry.isIntersecting && hasMoreProducts && !isLoading) {
                loadMoreProducts();
            }
        });
    }, {
        root: null,
        rootMargin: '100px', // Start loading when sentinel is 100px from viewport
        threshold: 0.1
    });

    scrollObserver.observe(sentinel);
}

function updateLoadMoreButton() {
    // Hide the load more button container since we're using scroll-based loading
    const loadMoreContainer = document.getElementById('loadMoreContainer');
    if (loadMoreContainer) {
        loadMoreContainer.style.display = 'none';
    }
}

// Products functionality with images and pagination
function displayProducts() {
    const container = document.getElementById('productsContainer');
    if (!container) return;

    let filteredProducts;

    if (searchTerm) {
        // Search mode - search all products
        filteredProducts = filterProductsBySearch(products);
    } else if (currentTertiaryCategory) {
        // Filter by specific tertiary category
        filteredProducts = products.filter(product => 
            product.primaryCategory === currentPrimaryCategory && 
            product.category === currentSecondaryCategory &&
            product.tertiaryCategory === currentTertiaryCategory
        );
    } else if (currentSecondaryCategory) {
        // Filter by specific secondary category
        filteredProducts = products.filter(product => 
            product.primaryCategory === currentPrimaryCategory && 
            product.category === currentSecondaryCategory
        );
    } else if (currentPrimaryCategory) {
        // Show all products for current primary category
        filteredProducts = products.filter(product => 
            product.primaryCategory === currentPrimaryCategory
        );
    } else {
        // No selection made
        return;
    }

    allFilteredProducts = filteredProducts;

    if (filteredProducts.length === 0) {
        const noResultsMessage = searchTerm 
            ? (currentLang === 'ar' ? 'ŸÑÿß ÿ™Ÿàÿ¨ÿØ ŸÖŸÜÿ™ÿ¨ÿßÿ™ ÿ™ÿ∑ÿßÿ®ŸÇ ÿßŸÑÿ®ÿ≠ÿ´' : 'No products match your search')
            : (currentLang === 'ar' ? 'ŸÑÿß ÿ™Ÿàÿ¨ÿØ ŸÖŸÜÿ™ÿ¨ÿßÿ™ ŸÅŸä Ÿáÿ∞Ÿá ÿßŸÑŸÅÿ¶ÿ©' : 'No products found in this category');

        container.innerHTML = `
            <div class="no-products">
                <p>${noResultsMessage}</p>
            </div>
        `;

        document.getElementById('loadMoreContainer').style.display = 'none';
        return;
    }

    // Reset pagination and display first page
    resetPagination();
    displayedProducts = filteredProducts.slice(0, PRODUCTS_PER_PAGE);
    renderProducts(displayedProducts);

    // Check if there are more products and setup scroll observer
    if (filteredProducts.length > PRODUCTS_PER_PAGE) {
        hasMoreProducts = true;
        setupScrollObserver();
    } else {
        hasMoreProducts = false;
    }

    updateLoadMoreButton();
}

function renderProducts(productsToRender) {
    const container = document.getElementById('productsContainer');
    if (!container) return;

    container.innerHTML = productsToRender.map(product => `
        <div class="product-card" data-category="${product.category}">
            <div class="product-image">
                <img src="${product.image}" alt="${product.name[currentLang]}" loading="lazy">
                <div class="product-id">ID: ${product.id}</div>
            </div>
            <div class="product-info">
                <h3 class="product-name">${product.name[currentLang]}</h3>
                <p class="product-description">${product.description[currentLang]}</p>
                <div class="product-price">${product.price}</div>
            </div>
            <button class="whatsapp-btn" onclick="openWhatsAppOrder('${product.id}')" data-en="Order via WhatsApp" data-ar="ÿßÿ∑ŸÑÿ® ÿπÿ®ÿ± Ÿàÿßÿ™ÿ≥ÿßÿ®">
                <span class="whatsapp-icon">üì±</span>
                ${currentLang === 'ar' ? 'ÿßÿ∑ŸÑÿ® ÿπÿ®ÿ± Ÿàÿßÿ™ÿ≥ÿßÿ®' : 'Order via WhatsApp'}
            </button>
        </div>
    `).join('');
}

// Setup classification bars
function setupClassificationBars() {
    // Only setup if primary bar exists (products page)
    const primaryBar = document.getElementById('primaryBar');
    if (!primaryBar) return;

    const primaryButtons = document.querySelectorAll('.primary-btn');
    primaryButtons.forEach(button => {
        // Check if listener already attached to prevent duplicates
        if (!button.dataset.initialized) {
            button.dataset.initialized = 'true';
            button.addEventListener('click', () => {
                const primaryCategory = button.getAttribute('data-primary');
                selectPrimaryCategory(primaryCategory);
            });
        }
    });

    // Setup load more button (keep as fallback, but hide by default)
    const loadMoreBtn = document.getElementById('loadMoreBtn');
    if (loadMoreBtn && !loadMoreBtn.dataset.initialized) {
        loadMoreBtn.dataset.initialized = 'true';
        loadMoreBtn.addEventListener('click', loadMoreProducts);
    }
}

// Best-selling products for homepage
function displayBestSellers() {
    const container = document.getElementById('bestSellersContainer');
    if (!container) return;

    container.innerHTML = bestSellingProducts.map(product => `
        <div class="bestseller-card">
            <div class="product-image">
                <img src="${product.image}" alt="${product.name[currentLang]}" loading="lazy">
                ${product.discount ? `<div class="discount-badge">${product.discount} ${currentLang === 'ar' ? 'ÿÆÿµŸÖ' : 'OFF'}</div>` : ''}
                <div class="product-id">ID: ${product.id}</div>
            </div>
            <div class="product-info">
                <h3 class="product-name">${product.name[currentLang]}</h3>
                <p class="product-description">${product.description[currentLang]}</p>
                <div class="price-section">
                    <div class="current-price">${product.price}</div>
                    ${product.originalPrice ? `<div class="original-price">${product.originalPrice}</div>` : ''}
                </div>
            </div>
            <button class="whatsapp-btn" onclick="openWhatsAppOrder('${product.id}')" data-en="Order via WhatsApp" data-ar="ÿßÿ∑ŸÑÿ® ÿπÿ®ÿ± Ÿàÿßÿ™ÿ≥ÿßÿ®">
                <span class="whatsapp-icon">üì±</span>
                ${currentLang === 'ar' ? 'ÿßÿ∑ŸÑÿ® ÿπÿ®ÿ± Ÿàÿßÿ™ÿ≥ÿßÿ®' : 'Order via WhatsApp'}
            </button>
        </div>
    `).join('');
}

// Contact form functionality
function handleContactForm(event) {
    event.preventDefault();

    const formData = new FormData(event.target);
    const data = Object.fromEntries(formData);


}

// Initialize language from localStorage
const savedLang = localStorage.getItem("lang") || "en";
currentLang = savedLang;
if (currentLang === "ar") {
    document.documentElement.setAttribute("lang", "ar");
    document.documentElement.setAttribute("dir", "rtl");
} else {
    document.documentElement.setAttribute("lang", "en");
    document.documentElement.setAttribute("dir", "ltr");
}

// Initialize page functionality
document.addEventListener('DOMContentLoaded', async function() {
    // Initialize dark mode from saved preference
    initializeDarkMode();

    // Load data from JSON files
    const dataLoaded = await loadData();
    if (!dataLoaded) {
        console.error('Failed to load data');
        return;
    }

    // Setup classification bars
    setupClassificationBars();

    // Setup search functionality
    setupSearchListeners();

    // Ensure primary bar is visible initially (only if elements exist)
    const primaryBar = document.getElementById('primaryBar');
    const secondaryBar = document.getElementById('secondaryBar');
    const tertiaryBar = document.getElementById('tertiaryBar');
    if (primaryBar) {
        primaryBar.style.display = 'block';
    }
    if (secondaryBar) {
        secondaryBar.style.display = 'none';
    }
    if (tertiaryBar) {
        tertiaryBar.style.display = 'none';
    }

    // Initialize best sellers display on homepage
    displayBestSellers();

    // Set up contact form
    const contactForm = document.getElementById('contactForm');
    if (contactForm) {
        contactForm.addEventListener('submit', handleContactForm);
    }

    // Update form placeholders on page load
    updateFormContent();

    // Update text content for current language
    updateTextContent();

    // Display initial message since no category is selected (only on products page)
    const container = document.getElementById('productsContainer');
    if (container && primaryBar) {
        container.innerHTML = `
            <div class="no-products">
                <p data-en="Please select a category above to view products" data-ar="Ÿäÿ±ÿ¨Ÿâ ÿßÿÆÿ™Ÿäÿßÿ± ŸÅÿ¶ÿ© ÿ£ÿπŸÑÿßŸá ŸÑÿπÿ±ÿ∂ ÿßŸÑŸÖŸÜÿ™ÿ¨ÿßÿ™">
                    ${currentLang === 'ar' ? 'Ÿäÿ±ÿ¨Ÿâ ÿßÿÆÿ™Ÿäÿßÿ± ŸÅÿ¶ÿ© ÿ£ÿπŸÑÿßŸá ŸÑÿπÿ±ÿ∂ ÿßŸÑŸÖŸÜÿ™ÿ¨ÿßÿ™' : 'Please select a category above to view products'}
                </p>
            </div>
        `;
    }
});

admin.css.content:
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    min-height: 100vh;
    padding: 20px;
    direction: rtl;
}

.admin-container {
    max-width: 1400px;
    margin: 0 auto;
}

.admin-header {
    background: white;
    border-radius: 15px;
    padding: 30px;
    margin-bottom: 30px;
    box-shadow: 0 8px 25px rgba(0, 0, 0, 0.1);
    display: flex;
    justify-content: space-between;
    align-items: center;
    flex-wrap: wrap;
    gap: 20px;
}

.header-content h1 {
    color: #2d3748;
    font-size: 2rem;
    margin-bottom: 5px;
}

.header-subtitle {
    color: #718096;
    font-size: 0.95rem;
}

.btn {
    padding: 12px 24px;
    border: none;
    border-radius: 8px;
    font-size: 1rem;
    font-weight: 600;
    cursor: pointer;
    transition: all 0.3s ease;
    display: inline-flex;
    align-items: center;
    gap: 8px;
}

.btn-primary {
    background: linear-gradient(135deg, #3f8439 0%, #11b023 100%);
    color: white;
}

.btn-primary:hover {
    transform: translateY(-2px);
    box-shadow: 0 6px 20px rgba(63, 132, 57, 0.4);
}

.btn-secondary {
    background: #e2e8f0;
    color: #4a5568;
}

.btn-secondary:hover {
    background: #cbd5e0;
}

.btn-danger {
    background: linear-gradient(135deg, #f56565 0%, #e53e3e 100%);
    color: white;
}

.btn-danger:hover {
    transform: translateY(-2px);
    box-shadow: 0 6px 20px rgba(245, 101, 101, 0.4);
}

.btn-warning {
    background: linear-gradient(135deg, #ed8936 0%, #dd6b20 100%);
    color: white;
}

.btn-warning:hover {
    transform: translateY(-2px);
    box-shadow: 0 6px 20px rgba(237, 137, 54, 0.4);
}

.btn-icon {
    font-size: 1.1rem;
}

.stats-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 20px;
    margin-bottom: 30px;
}

.stat-card {
    background: white;
    border-radius: 12px;
    padding: 25px;
    box-shadow: 0 4px 15px rgba(0, 0, 0, 0.1);
    display: flex;
    align-items: center;
    gap: 20px;
    transition: transform 0.3s ease;
}

.stat-card:hover {
    transform: translateY(-5px);
}

.stat-icon {
    font-size: 2.5rem;
    width: 70px;
    height: 70px;
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
}

.stat-info h3 {
    font-size: 2rem;
    color: #2d3748;
    margin-bottom: 5px;
}

.stat-info p {
    color: #718096;
    font-size: 0.9rem;
}

.search-filter-section {
    background: white;
    border-radius: 12px;
    padding: 20px;
    margin-bottom: 30px;
    box-shadow: 0 4px 15px rgba(0, 0, 0, 0.1);
    display: flex;
    gap: 20px;
    flex-wrap: wrap;
}

.search-box {
    flex: 1;
    min-width: 250px;
}

.search-box input {
    width: 100%;
    padding: 12px 20px;
    border: 2px solid #e2e8f0;
    border-radius: 8px;
    font-size: 1rem;
    transition: border-color 0.3s ease;
}

.search-box input:focus {
    outline: none;
    border-color: #3f8439;
}

.filter-select {
    padding: 12px 20px;
    border: 2px solid #e2e8f0;
    border-radius: 8px;
    font-size: 1rem;
    cursor: pointer;
    min-width: 200px;
    background: white;
}

.filter-select:focus {
    outline: none;
    border-color: #3f8439;
}

.products-table-container {
    background: white;
    border-radius: 12px;
    box-shadow: 0 4px 15px rgba(0, 0, 0, 0.1);
    overflow: hidden;
}

.products-table {
    width: 100%;
    border-collapse: collapse;
}

.products-table thead {
    background: linear-gradient(135deg, #3f8439 0%, #11b023 100%);
    color: white;
}

.products-table th {
    padding: 16px;
    text-align: right;
    font-weight: 600;
    font-size: 0.95rem;
}

.products-table tbody tr {
    border-bottom: 1px solid #e2e8f0;
    transition: background-color 0.2s ease;
}

.products-table tbody tr:hover {
    background-color: #f7fafc;
}

.products-table td {
    padding: 16px;
    text-align: right;
}

.product-img {
    width: 60px;
    height: 60px;
    object-fit: cover;
    border-radius: 8px;
}

.product-name {
    font-weight: 600;
    color: #2d3748;
}

.product-desc {
    color: #718096;
    font-size: 0.9rem;
}

.product-price {
    font-weight: 700;
    color: #3f8439;
    font-size: 1.1rem;
}

.product-category {
    background: #e6fffa;
    color: #234e52;
    padding: 6px 12px;
    border-radius: 20px;
    font-size: 0.85rem;
    display: inline-block;
}

.action-buttons {
    display: flex;
    gap: 8px;
    justify-content: center;
}

.action-buttons .btn {
    padding: 8px 16px;
    font-size: 0.9rem;
}

.modal {
    display: none;
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.5);
    z-index: 1000;
    align-items: center;
    justify-content: center;
}

.modal.active {
    display: flex;
}

.modal-content {
    background: white;
    border-radius: 15px;
    width: 90%;
    max-width: 800px;
    max-height: 90vh;
    overflow-y: auto;
    box-shadow: 0 15px 50px rgba(0, 0, 0, 0.3);
    animation: slideIn 0.3s ease;
}

@keyframes slideIn {
    from {
        transform: translateY(-50px);
        opacity: 0;
    }
    to {
        transform: translateY(0);
        opacity: 1;
    }
}

.modal-header {
    background: linear-gradient(135deg, #3f8439 0%, #11b023 100%);
    color: white;
    padding: 25px;
    border-radius: 15px 15px 0 0;
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.modal-header h2 {
    font-size: 1.5rem;
}

.close-btn {
    background: transparent;
    border: none;
    color: white;
    font-size: 2rem;
    cursor: pointer;
    width: 40px;
    height: 40px;
    display: flex;
    align-items: center;
    justify-content: center;
    border-radius: 50%;
    transition: background 0.3s ease;
}

.close-btn:hover {
    background: rgba(255, 255, 255, 0.2);
}

.form-grid {
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    gap: 20px;
    padding: 25px;
}

.form-group {
    display: flex;
    flex-direction: column;
}

.form-group.full-width {
    grid-column: 1 / -1;
}

.form-group label {
    margin-bottom: 8px;
    color: #2d3748;
    font-weight: 500;
    font-size: 0.95rem;
}

.form-group input,
.form-group textarea,
.form-group select {
    padding: 12px;
    border: 2px solid #e2e8f0;
    border-radius: 8px;
    font-size: 1rem;
    transition: border-color 0.3s ease;
}

.form-group input:focus,
.form-group textarea:focus,
.form-group select:focus {
    outline: none;
    border-color: #3f8439;
}

.form-group textarea {
    resize: vertical;
    font-family: inherit;
}

.modal-actions {
    padding: 0 25px 25px;
    display: flex;
    gap: 15px;
    justify-content: flex-end;
}

.notification {
    position: fixed;
    top: 20px;
    left: 50%;
    transform: translateX(-50%);
    background: white;
    padding: 18px 30px;
    border-radius: 10px;
    box-shadow: 0 10px 30px rgba(0, 0, 0, 0.2);
    z-index: 2000;
    display: none;
    align-items: center;
    gap: 12px;
    font-weight: 500;
    animation: slideDown 0.3s ease;
}

@keyframes slideDown {
    from {
        transform: translate(-50%, -100px);
        opacity: 0;
    }
    to {
        transform: translate(-50%, 0);
        opacity: 1;
    }
}

.notification.show {
    display: flex;
}

.notification.success {
    background: #c6f6d5;
    color: #22543d;
}

.notification.error {
    background: #fed7d7;
    color: #742a2a;
}

.notification.warning {
    background: #feebc8;
    color: #744210;
}

.loading-row td {
    padding: 60px 20px;
}

.loader {
    width: 50px;
    height: 50px;
    border: 4px solid #e2e8f0;
    border-top-color: #3f8439;
    border-radius: 50%;
    animation: spin 1s linear infinite;
    margin: 0 auto 15px;
}

@keyframes spin {
    to {
        transform: rotate(360deg);
    }
}

.text-center {
    text-align: center;
}

.no-products {
    text-align: center;
    padding: 40px;
    color: #718096;
}

.no-products p {
    font-size: 1.1rem;
    margin-bottom: 20px;
}

@media (max-width: 768px) {
    .admin-header {
        flex-direction: column;
        text-align: center;
    }

    .form-grid {
        grid-template-columns: 1fr;
    }

    .stats-grid {
        grid-template-columns: 1fr;
    }

    .products-table-container {
        overflow-x: auto;
    }

    .products-table {
        min-width: 800px;
    }

    .search-filter-section {
        flex-direction: column;
    }

    .filter-select {
        width: 100%;
    }
}

styles.css.content:
/* Reset and Base Styles */
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
    line-height: 1.6;
    color: #333;
    background-color: #f8f9fa;
    transition: all 0.3s ease;
}

/* Arabic Font Support */
[dir="rtl"] {
    font-family: 'Segoe UI', 'Tahoma', 'Arial Unicode MS', sans-serif;
}

/* Dark Mode Variables */
:root {
    --bg-primary: #f8f9fa;
    --bg-secondary: white;
    --text-primary: #333;
    --text-secondary: #666;
    --border-color: #f0f0f0;
    --card-shadow: rgba(0, 0, 0, 0.1);
    --hero-bg: linear-gradient(135deg, #3f8439 0%, #11b023 100%);
}

[data-theme="dark"] {
    --bg-primary: #121212;
    --bg-secondary: #1e1e1e;
    --text-primary: #ffffff;
    --text-secondary: #b3b3b3;
    --border-color: #333;
    --card-shadow: rgba(0, 0, 0, 0.3);
    --hero-bg: linear-gradient(135deg, #3f8439 0%, #11b023 100%);
}

body {
    background-color: var(--bg-primary);
    color: var(--text-primary);
}

/* Header Styles */
header {
    background: var(--bg-secondary);
    color: var(--text-primary);
    padding: 1rem 0;
    box-shadow: 0 2px 10px var(--card-shadow);
    position: sticky;
    top: 0;
    z-index: 1000;
}

.navbar {
    display: flex;
    justify-content: space-between;
    align-items: center;
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 20px;
    flex-wrap: wrap;
}

.logo h2 {
    font-size: 1.8rem;
    font-weight: 700;
    letter-spacing: -0.5px;
    color: #3f8439;
}

.nav-links {
    display: flex;
    list-style: none;
    gap: 30px;
    align-items: center;
}

.nav-links a {
    color: var(--text-primary);
    text-decoration: none;
    font-weight: 500;
    padding: 8px 16px;
    border-radius: 25px;
    transition: all 0.3s ease;
    position: relative;
}

.nav-links a:hover {
    color: #3f8439;
    background: var(--bg-primary);
}

.nav-links a.active {
    background: #3f8439;
    color: white;
}

/* Header Controls */
.header-controls {
    display: flex;
    align-items: center;
    gap: 15px;
}

.dark-mode-btn,
.header-controls button {
    background: var(--bg-primary);
    border: 2px solid var(--border-color);
    color: var(--text-primary);
    padding: 8px 16px;
    border-radius: 25px;
    cursor: pointer;
    font-weight: 500;
    transition: all 0.3s ease;
    display: flex;
    align-items: center;
    gap: 5px;
}

.dark-mode-btn {
    padding: 8px 12px;
    min-width: auto;
}

.dark-mode-btn:hover,
.header-controls button:hover {
    background: #3f8439;
    color: white;
    border-color: #3f8439;
}

.dark-mode-icon {
    font-size: 1.2rem;
    transition: transform 0.3s ease;
}

[data-theme="dark"] .dark-mode-icon {
    transform: rotate(180deg);
}

/* Hero Section */
.hero {
    background: var(--hero-bg);
    color: white;
    padding: 80px 20px;
    text-align: center;
    position: relative;
    overflow: hidden;
}

.hero::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 100 100"><defs><pattern id="grain" width="100" height="100" patternUnits="userSpaceOnUse"><circle cx="50" cy="50" r="1" fill="%23ffffff" opacity="0.1"/></pattern></defs><rect width="100" height="100" fill="url(%23grain)"/></svg>') repeat;
    pointer-events: none;
}

.hero-content {
    max-width: 800px;
    margin: 0 auto;
    position: relative;
    z-index: 1;
}

.hero-title {
    font-size: 3.5rem;
    font-weight: 300;
    margin-bottom: 20px;
    letter-spacing: -1px;
}

.hero-subtitle {
    font-size: 1.3rem;
    margin-bottom: 40px;
    opacity: 0.95;
    line-height: 1.6;
}

.browse-btn {
    display: inline-flex;
    align-items: center;
    gap: 10px;
    background: white;
    color: #3f8439;
    padding: 15px 30px;
    border-radius: 30px;
    text-decoration: none;
    font-weight: 600;
    font-size: 1.1rem;
    transition: all 0.3s ease;
    box-shadow: 0 4px 15px rgba(0, 0, 0, 0.1);
}

.browse-btn:hover {
    transform: translateY(-3px);
    box-shadow: 0 8px 25px rgba(0, 0, 0, 0.2);
}

.btn-icon {
    font-size: 1.2rem;
}

/* Main Content */
main {
    max-width: 1200px;
    margin: 0 auto;
    padding: 40px 20px;
    min-height: calc(100vh - 200px);
}

/* Why Choose Section */
.why-choose {
    padding: 80px 0;
    background: var(--bg-primary);
}

.section-container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 20px;
}

.section-title {
    text-align: center;
    font-size: 2.5rem;
    color: var(--text-primary);
    margin-bottom: 60px;
    font-weight: 300;
}

.features-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    gap: 40px;
    margin-top: 40px;
}

.feature-card {
    background: var(--bg-secondary);
    padding: 30px 25px;
    border-radius: 12px;
    text-align: center;
    box-shadow: 0 4px 20px var(--card-shadow);
    transition: all 0.3s ease;
    border: 1px solid var(--border-color);
}

.feature-card:hover {
    transform: translateY(-10px);
    box-shadow: 0 15px 40px rgba(0, 0, 0, 0.15);
}

.feature-icon {
    font-size: 3rem;
    margin-bottom: 20px;
    display: block;
}

.feature-card h3 {
    font-size: 1.3rem;
    color: #3f8439;
    margin-bottom: 15px;
    font-weight: 600;
}

.feature-card p {
    color: var(--text-secondary);
    line-height: 1.6;
    font-size: 0.95rem;
}

.page-title {
    text-align: center;
    margin-bottom: 40px;
}

.page-title h1 {
    font-size: 2.5rem;
    color: #11b023;
    margin-bottom: 10px;
    font-weight: 300;
}

/* Products Page Styles */
.classification-bar {
    background: var(--bg-secondary);
    padding: 20px;
    border-radius: 15px;
    box-shadow: 0 4px 20px var(--card-shadow);
    margin-bottom: 30px;
}

.category-buttons {
    display: flex;
    gap: 15px;
    justify-content: center;
    flex-wrap: wrap;
}

.category-btn {
    padding: 12px 24px;
    border: 2px solid var(--border-color);
    background: var(--bg-secondary);
    color: var(--text-secondary);
    border-radius: 30px;
    cursor: pointer;
    font-weight: 500;
    transition: all 0.3s ease;
    font-size: 0.95rem;
}

.category-btn:hover {
    border-color: #3f8439;
    color: #3f8439;
    transform: translateY(-2px);
}

.category-btn.active {
    background: #3f8439;
    color: white;
    border-color: #3f8439;
    box-shadow: 0 4px 15px rgba(74, 144, 226, 0.3);
}

.category-btn.back-btn {
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    color: white;
    border-color: #667eea;
    font-weight: 600;
    margin-left: 15px;
}

.category-btn.back-btn:hover {
    background: linear-gradient(135deg, #764ba2 0%, #667eea 100%);
    transform: translateY(-2px);
    box-shadow: 0 6px 20px rgba(102, 126, 234, 0.4);
}

[dir="rtl"] .category-btn.back-btn {
    margin-left: 0;
    margin-right: 15px;
}

/* Best Sellers Section */
.bestsellers-section {
    padding: 80px 0;
    background: var(--bg-secondary);
}

.section-subtitle {
    text-align: center;
    font-size: 1.2rem;
    color: var(--text-secondary);
    margin-bottom: 40px;
    max-width: 600px;
    margin-left: auto;
    margin-right: auto;
}

.bestsellers-grid {
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(250px, 1fr));
    gap: 25px;
    margin-top: 40px;
}

.bestseller-card {
    background: var(--bg-secondary);
    border-radius: 12px;
    padding: 0;
    box-shadow: 0 4px 20px var(--card-shadow);
    transition: all 0.3s ease;
    border: 1px solid var(--border-color);
    overflow: hidden;
}

.bestseller-card:hover {
    transform: translateY(-6px);
    box-shadow: 0 8px 30px var(--card-shadow);
}

/* Product Images */
.product-image {
    position: relative;
    height: 160px;
    overflow: hidden;
    border-radius: 12px 12px 0 0;
    margin-bottom: 15px;
}

.product-image img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    transition: transform 0.3s ease;
}

.product-card:hover .product-image img,
.bestseller-card:hover .product-image img {
    transform: scale(1.05);
}

.product-id {
    position: absolute;
    top: 10px;
    left: 10px;
    background: rgba(255, 255, 255, 0.9);
    color: #666;
    padding: 6px 12px;
    border-radius: 20px;
    font-size: 0.85rem;
    font-weight: 600;
    backdrop-filter: blur(10px);
}

[dir="rtl"] .product-id {
    left: auto;
    right: 10px;
}

.discount-badge {
    position: absolute;
    top: 10px;
    right: 10px;
    background: #e74c3c;
    color: white;
    padding: 6px 12px;
    border-radius: 20px;
    font-size: 0.85rem;
    font-weight: 600;
}

[dir="rtl"] .discount-badge {
    left: 10px;
    right: auto;
}

/* Products Container */
.products-container {
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(280px, 1fr));
    gap: 20px;
    margin-top: 30px;
}

.product-card {
    background: var(--bg-secondary);
    border-radius: 12px;
    padding: 0;
    box-shadow: 0 4px 20px var(--card-shadow);
    transition: all 0.3s ease;
    border: 1px solid var(--border-color);
    overflow: hidden;
}

.product-card:hover {
    transform: translateY(-6px);
    box-shadow: 0 8px 30px var(--card-shadow);
}

.product-info {
    padding: 15px;
    margin-bottom: 0;
}

.product-name {
    font-size: 1.1rem;
    color: #3f8439;
    margin-bottom: 8px;
    font-weight: 600;
    line-height: 1.3;
}

.product-description {
    color: var(--text-secondary);
    margin-bottom: 12px;
    line-height: 1.5;
    font-size: 0.9rem;
}

.product-price {
    font-size: 1.2rem;
    font-weight: 700;
    color: #27ae60;
    margin-bottom: 15px;
}

/* Price Section for Best Sellers */
.price-section {
    display: flex;
    align-items: center;
    gap: 10px;
    margin-bottom: 20px;
}

.current-price {
    font-size: 1.2rem;
    font-weight: 700;
    color: #27ae60;
}

.original-price {
    font-size: 1rem;
    color: var(--text-secondary);
    text-decoration: line-through;
    opacity: 0.7;
}

/* WhatsApp Button */
.whatsapp-btn {
    background: linear-gradient(135deg, #25d366 0%, #128c7e 100%);
    color: white;
    border: none;
    padding: 10px 20px;
    border-radius: 0 0 12px 12px;
    cursor: pointer;
    font-weight: 500;
    transition: all 0.3s ease;
    width: 100%;
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 6px;
    font-size: 0.9rem;
}

.whatsapp-btn:hover {
    background: linear-gradient(135deg, #128c7e 0%, #075e54 100%);
    transform: translateY(-2px);
    box-shadow: 0 6px 20px rgba(37, 211, 102, 0.4);
}

.whatsapp-icon {
    font-size: 1.2rem;
}

/* Load More Button */
.load-more-container {
    display: flex;
    justify-content: center;
    margin: 40px 0;
}

.load-more-btn {
    background: linear-gradient(135deg, #3f8439 0%, #11b023 100%);
    color: white;
    border: none;
    padding: 15px 30px;
    border-radius: 25px;
    font-size: 1rem;
    font-weight: 600;
    cursor: pointer;
    transition: all 0.3s ease;
    display: flex;
    align-items: center;
    gap: 8px;
}

.load-more-btn:hover {
    transform: translateY(-2px);
    box-shadow: 0 8px 25px rgba(63, 132, 57, 0.3);
}

.load-more-btn:disabled {
    background: var(--text-secondary);
    cursor: not-allowed;
    transform: none;
    box-shadow: none;
}

/* Page Content Styles */
.content-container {
    background: var(--bg-secondary);
    border-radius: 15px;
    padding: 40px;
    box-shadow: 0 6px 25px var(--card-shadow);
    margin-bottom: 30px;
}

/* About Page Styles */

.about-section {
    margin-bottom: 40px;
}

.about-section:last-child {
    margin-bottom: 0;
}

.about-section h2 {
    color: #3f8439;
    font-size: 1.8rem;
    margin-bottom: 20px;
    font-weight: 600;
    border-bottom: 3px solid #3f8439;
    padding-bottom: 10px;
    display: inline-block;
}

.about-section p {
    font-size: 1.1rem;
    line-height: 1.8;
    color: #555;
    margin-bottom: 15px;
}

.values-list {
    list-style: none;
    padding: 0;
}

.values-list li {
    background: #f8f9fa;
    margin-bottom: 15px;
    padding: 20px;
    border-radius: 10px;
    border-left: 4px solid #3f8439;
    font-size: 1.05rem;
    transition: all 0.3s ease;
}

[dir="rtl"] .values-list li {
    border-left: none;
    border-right: 4px solid #3f8439;
}

.values-list li:hover {
    background: #f0f7ff;
    transform: translateX(5px);
}

[dir="rtl"] .values-list li:hover {
    transform: translateX(-5px);
}

/* Contact Page Styles */
.contact-info {
    margin-bottom: 40px;
}

.contact-section {
    margin-bottom: 30px;
}

.contact-section h2 {
    color: #2c5aa0;
    font-size: 1.8rem;
    margin-bottom: 15px;
    font-weight: 600;
}

.contact-section p {
    font-size: 1.1rem;
    line-height: 1.8;
    color: #555;
}

.contact-details {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 25px;
    margin-top: 30px;
}

.contact-item {
    background: #f8f9fa;
    padding: 25px;
    border-radius: 12px;
    text-align: center;
    transition: all 0.3s ease;
}

.contact-item:hover {
    background: #f0f7ff;
    transform: translateY(-5px);
}

.contact-item h3 {
    color: #3f8439;
    font-size: 1.2rem;
    margin-bottom: 10px;
    font-weight: 600;
}

.contact-item p {
    color: #666;
    font-size: 1rem;
    margin: 5px 0;
}

.contact-form-section {
    margin-top: 40px;
    padding-top: 40px;
    border-top: 2px solid #f0f0f0;
}

.contact-form-section h2 {
    color: #3f8439;
    font-size: 1.8rem;
    margin-bottom: 25px;
    font-weight: 600;
    text-align: center;
}

.contact-form {
    max-width: 600px;
    margin: 0 auto;
}

.form-group {
    margin-bottom: 25px;
}

.form-group label {
    display: block;
    margin-bottom: 8px;
    color: #333;
    font-weight: 500;
    font-size: 1rem;
}

.form-group input,
.form-group select,
.form-group textarea {
    width: 100%;
    padding: 12px 16px;
    border: 2px solid #e1e5e9;
    border-radius: 8px;
    font-size: 1rem;
    transition: all 0.3s ease;
    background: white;
}

.form-group input:focus,
.form-group select:focus,
.form-group textarea:focus {
    outline: none;
    border-color: #3f8439;
    box-shadow: 0 0 0 3px rgba(74, 144, 226, 0.1);
}

.submit-btn {
    background: linear-gradient(135deg, #3f8439 0%, #11b023 100%);
    color: white;
    border: none;
    padding: 15px 40px;
    border-radius: 25px;
    font-size: 1.1rem;
    font-weight: 600;
    cursor: pointer;
    transition: all 0.3s ease;
    display: block;
    margin: 30px auto 0;
}

.submit-btn:hover {
    transform: translateY(-2px);
    box-shadow: 0 6px 20px rgba(74, 144, 226, 0.4);
}

/* Footer */
footer {
    background: var(--bg-secondary);
    color: var(--text-primary);
    text-align: center;
    padding: 20px;
    margin-top: 50px;
    border-top: 1px solid var(--border-color);
}

footer p {
    font-size: 0.95rem;
}

/* Responsive Design */
@media (max-width: 768px) {
    .navbar {
        flex-direction: column;
        gap: 15px;
        padding: 15px;
    }

    .nav-links {
        gap: 15px;
    }

    .page-title h1 {
        font-size: 2rem;
    }

    .products-container {
        grid-template-columns: 1fr;
        gap: 20px;
    }

    .category-buttons {
        flex-direction: column;
        align-items: center;
    }

    .category-btn {
        width: 100%;
        max-width: 300px;
    }

    .content-container {
        padding: 25px;
    }

    .contact-details {
        grid-template-columns: 1fr;
    }
}

@media (max-width: 480px) {
    main {
        padding: 20px 15px;
    }

    .page-title h1 {
        font-size: 1.6rem;
    }

    .product-card {
        padding: 20px;
    }

    .content-container {
        padding: 20px;
    }
}

/* RTL (Arabic) Specific Styles */
[dir="rtl"] {
    text-align: right;
}

[dir="rtl"] .navbar {
    direction: rtl;
}

[dir="rtl"] .nav-links {
    direction: rtl;
}

[dir="rtl"] .category-buttons {
    direction: rtl;
}

[dir="rtl"] .products-container {
    direction: rtl;
}

[dir="rtl"] .contact-details {
    direction: rtl;
}

[dir="rtl"] .form-group {
    text-align: right;
}

[dir="rtl"] .form-group input,
[dir="rtl"] .form-group select,
[dir="rtl"] .form-group textarea {
    text-align: right;
}

/* Loading Animation */
@keyframes fadeInUp {
    from {
        opacity: 0;
        transform: translateY(30px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

.product-card,
.contact-item,
.about-section {
    animation: fadeInUp 0.6s ease forwards;
}



/* social icon  */


 .social-icons {
      position: fixed;
      top: 50%;
      left: 20px;
      transform: translateY(-50%);
      display: flex;
      flex-direction: column;
      gap: 15px;
    }
    .social-icons a {
      text-decoration: none;
      font-size: 28px;
      color: white;
      background: #333;
      width: 45px;
      height: 45px;
      border-radius: 50%;
      display: flex;
      align-items: center;
      justify-content: center;
      transition: 0.3s;
    }
    .social-icons a:hover {
      background: #007bff;
      transform: scale(1.1);
    }

    /* Search container */
.search-container {
    margin: 2rem 0;
    display: flex;
    justify-content: center;
}

.search-box {
    display: flex;
    gap: 1rem;
    align-items: center;
    max-width: 500px;
    width: 100%;
}

.search-box input {
    flex: 1;
    padding: 0.8rem 1rem;
    border: 2px solid var(--border-color);
    border-radius: 25px;
    font-size: 1rem;
    background-color: var(--bg-color);
    color: var(--text-color);
    transition: all 0.3s ease;
}

.search-box input:focus {
    outline: none;
    border-color: var(--primary-color);
    box-shadow: 0 0 0 3px rgba(44, 90, 160, 0.1);
}

.search-box button {
    padding: 0.8rem 1.5rem;
    background-color: var(--primary-color);
    color: blue;
    border: none;
    border-radius: 20px;
    font-size: 0.9rem;
    cursor: pointer;
    transition: all 0.3s ease;
}

.search-box button:hover {
    background-color: #3f8439;
    transform: translateY(-1px);
}

/* No products message */
.no-products {
    text-align: center;
    padding: 40px 20px;
    color: var(--text-secondary);
    font-size: 1.1rem;
}

/* Loading state */
.loading {
    text-align: center;
    padding: 20px;
    color: var(--text-secondary);
}

.loading::after {
    content: "...";
    animation: loading 1.5s infinite;
}

@keyframes loading {
    0%, 20% { opacity: 0; }
    50% { opacity: 1; }
    80%, 100% { opacity: 0; }
}
